package org.aion.zero.impl.consensus;

import static com.google.common.truth.Truth.assertThat;
import static org.aion.zero.impl.consensus.MasteryConsensusTest.hexEncodedStateData;

import java.math.BigInteger;
import java.util.HashMap;
import java.util.Map;
import org.aion.log.AionLoggerFactory;
import org.aion.mcf.core.ImportResult;
import org.aion.mcf.valid.TransactionTypeRule;
import org.aion.util.conversions.Hex;
import org.aion.vm.LongLivedAvm;
import org.aion.zero.impl.StandaloneBlockchain;
import org.aion.zero.impl.StandaloneBlockchain.Builder;
import org.aion.zero.impl.types.AionBlock;
import org.aion.zero.impl.types.AionBlockSummary;
import org.aion.zero.types.AionTxReceipt;
import org.apache.commons.lang3.tuple.Pair;
import org.junit.AfterClass;
import org.junit.BeforeClass;
import org.junit.Test;

/**
 * Unit tests using interesting data from the <b>mainnet</b> network.
 *
 * @author Alexandra Roatis
 */
public class MainnetConsensusTest {
    @BeforeClass
    public static void setup() {
        // reduce default logging levels
        Map<String, String> cfg = new HashMap<>();
        cfg.put("API", "ERROR");
        cfg.put("CONS", "ERROR");
        cfg.put("DB", "ERROR");
        cfg.put("GEM", "ERROR");
        cfg.put("P2P", "ERROR");
        cfg.put("ROOT", "ERROR");
        cfg.put("SYNC", "ERROR");
        cfg.put("TX", "ERROR");
        cfg.put("VM", "DEBUG");
        AionLoggerFactory.init(cfg);

        LongLivedAvm.createAndStartLongLivedAvm();
    }

    @AfterClass
    public static void tearDownAvm() {
        LongLivedAvm.destroy();
    }

    /**
     * Test for importing block <a href="https://mainnet.aion.network/#/block/1088454">1088454</a>.
     */
    @Test
    public void testImportBlock1088454() {
        TransactionTypeRule.disallowAVMContractTransaction();

        // data extracted from the mastery network
        AionBlock block1088452 =
                new AionBlock(
                        Hex.decode(
                                ""));
        BigInteger tdAt1088452 = new BigInteger("11658225838160");

        AionBlock block1088453 =
                new AionBlock(
                        Hex.decode(
                                ""));
        BigInteger tdAt1088453 = new BigInteger("11658244332995");

        byte[] trieDataAt1088453 = Hex.decode(hexEncodedStateData("mainnetStateAt1088453.txt"));

        // block to import
        AionBlock block1088454 =
                new AionBlock(
                        Hex.decode(
                                ""));
        BigInteger expectedTdAt1088454 = new BigInteger("11658262827830");

        // setup blockchain with state before imported block
        StandaloneBlockchain blockchain =
                new Builder()
                        .withState(
                                block1088452,
                                tdAt1088452,
                                block1088453,
                                tdAt1088453,
                                trieDataAt1088453)
                        .withAvmEnabled()
                        .build()
                        .bc;

        // import block
        Pair<ImportResult, AionBlockSummary> results =
                blockchain.tryToConnectAndFetchSummary(block1088454);
        assertThat(results.getLeft()).isEqualTo(ImportResult.IMPORTED_BEST);

        // collect the consensus information from the block & receipt.
        AionBlockSummary blockSummary = results.getRight();

        long[] energyPerTx =
                new long[] {
                    496_634L, 116_149L, 496_634L, 116_149L, 496_634L, 116_149L, 496_634L, 116_149L,
                    496_634L, 21_000L, 21_000L, 21_000L, 21_000L, 21_000L, 21_000L, 21_000L,
                    21_000L
                };
        byte[][] postTxStates =
                new byte[][] {
                    Hex.decode("5802347050ee7eb82ee28da18e7287caf2985bf145d0de695d5d25011551034c"),
                    Hex.decode("b19582b990127716840fe6a539aeb7a51dfcd47f5f3d96bb2ef2a42137da95ff"),
                    Hex.decode("7029ef5a462bc8cef74c121c5cafb91321deb35cf1c820596554c0f2c260799c"),
                    Hex.decode("fe619299a305035b16de39e83ec4916dca94f4e65e0e505f4ab9dbc66b7f267b"),
                    Hex.decode("7fc4895e5828d28c2bffb9249d4e242f8532c92b6a4b19e104b23474435078bf"),
                    Hex.decode("42fb5a4593141d2f8aa048fce39697da7d0c6fcf4153610e028fc35958644fd6"),
                    Hex.decode("a7be20b15fd3d518094472882187bb743e14814c6e355b4fc7d16b5a07e53954"),
                    Hex.decode("5da41af05a2f6b779c4f63cdd386652854b9eea126591fc257b95c6653292778"),
                    Hex.decode("d83ab8b72ff2d3c2afa2097e6ba1ae8896b83e7a0233f2ee3d642b1304e834ad"),
                    Hex.decode("65c19b0855d3f1ae85be4a37c731f3604ac990d2df029f83a5ed2963c7fa387e"),
                    Hex.decode("28432ea2812be6a31fec670ecaf4effdd017e1816026047adb6b20591084c6ae"),
                    Hex.decode("a1cc1e20c901acfb027a94ad10b6fa99dc7a02c1bd84f90daf1fa0ff70b488be"),
                    Hex.decode("4cb6817f7deebf6c2aedcf2e63a5ecc0d55f1d618d618cbcb28afc4e069b175f"),
                    Hex.decode("26204319d8d0f05c334dffb507e8b5b312742eb8f15207dd26f550ae93c561bc"),
                    Hex.decode("3a9847f9af67ad9862e2b2af4fac55e8e3f529e228008f20ec33d043aa7d084a"),
                    Hex.decode("d0a20f35588d9b62305a858532c469ee267f05d6e917685c9e8e1056968b1873"),
                    Hex.decode("7fb1f1b151335b327178093f5eb317d362bc9ffd2beedc4f3362363f09a3b13f")
                };
        byte[][] txReceiptTrie =
                new byte[][] {
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            ""),
                    Hex.decode(
                            "")
                };

        // check the receipts
        for (int i = 0; i < 17; i++) {
            AionTxReceipt receipt = blockSummary.getSummaries().get(i).getReceipt();
            assertThat(receipt.isSuccessful()).isTrue();
            assertThat(receipt.getEnergyUsed()).isEqualTo(energyPerTx[i]);
            assertThat(receipt.getPostTxState()).isEqualTo(postTxStates[i]);
            assertThat(receipt.getReceiptTrieEncoded()).isEqualTo(txReceiptTrie[i]);
        }

        byte[] stateRoot = blockSummary.getBlock().getStateRoot();
        byte[] blockReceiptsRoot = blockSummary.getBlock().getReceiptsRoot();

        // verify the consensus information.
        String expectedRoot = "8d2ebcd33aae15e660d1bbc305d9e292341d445287294ce6eb53ef803a82951c";
        String expectedReceiptsRoot =
                "81b1fe0467ae46709932e99917122c33b39788e92aac4e05e429b1030f25a5a8";
        assertThat(stateRoot).isEqualTo(Hex.decode(expectedRoot));
        assertThat(blockReceiptsRoot).isEqualTo(Hex.decode(expectedReceiptsRoot));

        // checking repository state and total difficulty
        assertThat(blockchain.getRepository().getRoot()).isEqualTo(Hex.decode(expectedRoot));
        assertThat(blockchain.getTotalDifficulty()).isEqualTo(expectedTdAt1088454);
    }
}
